# Nginx –∫–æ–Ω—Ñ—ñ–≥—É—Ä–∞—Ü—ñ—è –¥–ª—è React SPA \(Single Page Application\)

server {
    listen 3000;
    server_name localhost;

    root /usr/share/nginx/html;
    index index.html;

    # Gzip compression –¥–ª—è –∫—Ä–∞—â–æ—ó –ø—Ä–æ–¥—É–∫—Ç–∏–≤–Ω–æ—Å—Ç—ñ
    gzip on;
    gzip_vary on;
    gzip_min_length 1024;
    gzip_types
        text/plain
        text/css
        text/xml
        text/javascript
        application/javascript
        application/xml+rss
        application/json
        image/svg+xml;

    # Security headers
    add_header X-Frame-Options "SAMEORIGIN" always;
    add_header X-Content-Type-Options "nosniff" always;
    add_header X-XSS-Protection "1; mode=block" always;
    add_header Referrer-Policy "no-referrer-when-downgrade" always;

    # React Router - –≤—Å—ñ —Ä–æ—É—Ç–∏ —á–µ—Ä–µ–∑ index.html
    location / {
        try_files $uri $uri/ /index.html;
    }

    # –ö–µ—à—É–≤–∞–Ω–Ω—è —Å—Ç–∞—Ç–∏—á–Ω–∏—Ö —Ñ–∞–π–ª—ñ–≤ \(JS, CSS, fonts, images\)
    location ~* \\.\(js|css|png|jpg|jpeg|gif|ico|svg|woff|woff2|ttf|eot\)$ {
        expires 1y;
        add_header Cache-Control "public, immutable";
        access_log off;
    }

    # –ó–∞–±–æ—Ä–æ–Ω–∏—Ç–∏ –¥–æ—Å—Ç—É–ø –¥–æ –ø—Ä–∏—Ö–æ–≤–∞–Ω–∏—Ö —Ñ–∞–π–ª—ñ–≤
    location ~ /\\. {
        deny all;
        access_log off;
        log_not_found off;
    }

    # Health check endpoint
    location /health {
        access_log off;
        return 200 "OK\\n";
        add_header Content-Type text/plain;
    }

    # –õ–æ–≥—É–≤–∞–Ω–Ω—è
    access_log /var/log/nginx/access.log;
    error_log /var/log/nginx/error.log warn;
}
EOF)
